mapper.select:
select f.id,f.user_id,f.goods_id,f.add_time add_time,f.referrer, g.name as name, g.list_pic_url as list_pic_url, g.goods_brief as goods_brief, g.retail_price as retail_price from nideshop_footprint f
left join nideshop_footprint max on f.goods_id = max.goods_id and f.user_id = max.user_id and max.add_time > f.add_time
left join nideshop_goods g on f.goods_id = g.id
and f.user_id = #{user_id}
and max.id is null
order by ${sidx} ${order}
order by f.id desc
limit #{offset}, #{limit}

select a.* ,b.username as user_name,c.name as goods_name from nideshop_footprint a left join nideshop_user b on a.user_id = b.id left join nideshop_goods c on a.goods_id = c.id
order by ${sidx} ${order}
order by a.id desc

select count(*) from nideshop_footprint f


        and f.user_id = #{user_id}


        and f.goods_id = #{goods_id}


        and f.referrer = #{referrer}

select f.id,f.user_id,f.goods_id,f.add_time add_time,f.referrer,
g.name as name, g.list_pic_url as list_pic_url, g.goods_brief as goods_brief,
g.retail_price as retail_price,
u.nickname as nickname,u.avatar as avatar
from nideshop_footprint f
left join nideshop_goods g on f.goods_id = g.id
left join nideshop_user u on u.id = f.user_id

    u.id is not null

        and f.user_id = #{user_id}


        and f.referrer = #{referrer}




        order by ${sidx} ${order}


        order by f.id desc



    limit #{offset}, #{limit}
